// Nav.js
import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Routes } from "react-router";
import "./NavBar.css";
import logo from "./assets/LLHorizontal.png";
import Home from "./home/Home";
import About from "./About";
import Specials from "./Specials";
import Order from "./Order";
import Login from "./Login";
import ReservationStepOne from "./ReservationStepOne";
import ReservationStepTwo from "./ReservationStepTwo";
import ReservationSuccess from "./ReservationSuccess";

function NavBar() {
  const [showNavBar, setShowNavBar] = useState(true);
  let lastScrollY = window.scrollY;

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      setShowNavBar(currentScrollY < lastScrollY || currentScrollY < 10);
      lastScrollY = currentScrollY;
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);
  return (
    <nav className={`horizontal-nav`}>
      <div className="logo-container">
        <img src={logo} alt="Little Lemon Logo" className="logo" />
      </div>
    <BrowserRouter>
    <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/specials" element={<Specials />} />
        <Route path="/order" element={<Order />} />
        <Route path="/login" element={<Login />} />
        <Route path="/reservationstepone" element={<ReservationStepOne />} />
        <Route path="/step-two" element={<ReservationStepTwo />} />
        <Route path="/success" element={<ReservationSuccess />} /> 
     </Routes>
    </BrowserRouter>
      </nav>
    );
  }


export default NavBar;
