// Menu.js
import React from 'react';
import './Menu.css';

const sections = {
  Starters: [
    { name: 'Lemon-Herb Hummus', desc: 'Creamy chickpeas with tahini, garlic, and fresh lemon juice, served with warm pita.', price: '£5.50' },
    { name: 'Caprese Skewers', desc: 'Cherry tomatoes, basil, and mozzarella with a balsamic glaze.', price: '£6.00' },
    { name: 'Stuffed Grape Leaves', desc: 'Vine leaves filled with herbed rice and lemon yogurt dip.', price: '£5.75' },
  ],
  Mains: [
    { name: 'Zesty Lemon Chicken', desc: 'Grilled chicken in lemon and garlic marinade, with roasted veggies.', price: '£12.50' },
    { name: 'Mediterranean Pasta', desc: 'Penne with sun-dried tomatoes, olives, feta, and lemon-garlic sauce.', price: '£11.00' },
    { name: 'Spiced Chickpea Tagine', desc: 'Apricots, cinnamon, and preserved lemon, served over couscous.', price: '£10.50' },
  ],
  Sides: [
    { name: 'Charred Broccoli with Lemon Zest', desc: '', price: '£4.00' },
    { name: 'Orzo Salad with Feta', desc: '', price: '£4.50' },
    { name: 'Rosemary Fries', desc: '', price: '£3.50' },
  ],
  Desserts: [
    { name: 'Little Lemon Tart', desc: 'Buttery crust, lemon curd, toasted meringue.', price: '£5.00' },
    { name: 'Baklava Bites', desc: 'Filo pastry, honey, pistachios.', price: '£4.75' },
    { name: 'Limoncello Sorbet', desc: 'Bright, citrusy refreshment.', price: '£4.25' },
  ],
  Beverages: [
    { name: 'Minted Iced Tea', desc: '', price: '£2.50' },
    { name: 'Citrus Juice', desc: '', price: '£2.75' },
    { name: 'Espresso & Herbal Teas', desc: '', price: '£2.00' },
  ]
};

const Menu = () => {
  return (
    <div className="menu-wrapper">
      <h1 className="title">Little Lemon</h1>
      <p className="lead-text">A refreshing menu full of vibrant flavours and Mediterranean charm.</p>
      {Object.entries(sections).map(([section, items]) => (
        <div key={section} className="menu-section">
          <h2 className="section-title">{section}</h2>
          <ul className="menu-list">
            {items.map(({ name, desc, price }) => (
              <li key={name} className="menu-card">
                <div className="card-header">
                  <span className="card-title">{name}</span>
                  <span className="price">{price}</span>
                </div>
                {desc && <p className="description">{desc}</p>}
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default Menu;
