        <li><Link to="/specials">Specials</Link></li>
        <li><Link to="/order">Order</Link></li>
        <li><Link to="/login">Login</Link></li>
        <li><Link to="/reservationstepone">Reservation</Link></li>
      </ul> */}
      </nav>
    );
  }


export default NavBar;
