// Nav.js
import React, { useState, useEffect } from "react";
import { Switch, Route, Link } from "react-router-dom";
import "./NavBar.css";
import logo from "./assets/LLHorizontal.png";
import App from "../App";
import about from "./About";
import menu from "./Menu";
import reservationstepone from "./ReservationStepOne";
import order from "./Order";
import login from "./Login";

function NavBar() {
  // const [showNavBar, setShowNavBar] = useState(true);
  // let lastScrollY = window.scrollY;

  // useEffect(() => {
  //   const handleScroll = () => {
  //     const currentScrollY = window.scrollY;
  //     setShowNavBar(currentScrollY < lastScrollY || currentScrollY < 10);
  //     lastScrollY = currentScrollY;
  //   };

  //   window.addEventListener("scroll", handleScroll);
  //   return () => window.removeEventListener("scroll", handleScroll);
  // }, []);
  return (
    <nav className={`vertical-nav`}>
      <div className="logo-container">
        <img src={logo} alt="Little Lemon Logo" className="logo" />
      </div>
      <switch>
        <Route path="/App" element={<Home />} />
        <Route path="/components/about" element={<About />} />
        <Route path="/components/menu" element={<Menu />} />
        <Route path="/components/reservationstepone" element={<ReservationStepOne />} />
        <Route path="/components/order" element={<Order />} />
        <Route path="/components/login" element={<Login />} />
      </switch>
    </nav>
  );
}

export default NavBar;
